package com.ifelse;

public class ForloopEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=229;
		
		System.out.println("list of even number from 121 to 229:");
		for(int i=121;i<=number;i++) {
			if(i%2==0) {
				System.out.println(i+ "");
			}
		}

	}

}
